<template>
    <div id="chart-builder">
        <TableList :loading="loading" v-if="!loading" />
        <div class="table-list ve-column" v-if="loading"></div>
        <ElementControl v-if="!loading" />
        <ElementPreview @saveSchema="saveSchema" v-if="!loading" />
    </div>
</template>

<script>
import draggable from "vuedraggable";

import TableList from "./tableList/TableList";
import ElementControl from "./controls/ElementControl";
import ElementPreview from "./elements/ElementPreview";
import store from "./store/store";
import List from "./index";
import { mapGetters } from "vuex";

export default {
    props: ["onCreate", "onUpdate", "src", "editMode"],
    methods: {
        showTable(table) {

        },

        saveSchema(name, schema) {
            schema.fields = this.fields;
            schema.filters = this.other.filters;

            let data = {
                name: name,
                schema: JSON.stringify(schema)
            };
            let submitUrl = this.$props.editMode
                ? this.$props.src
                : `/lambda/puzzle/schema/chart`;

            axios.post(submitUrl, data).then(o => {
                if (o.data.status) {
                    this.$Message.info("Амжилттай хадгаллаа");
                    this.$props.onCreate();
                } else {
                    this.$Message.info("Хадгалах үед алдаа гарлаа!");
                }
            });
        },
        init() {
            if (this.$props.editMode == true) {
                axios
                    .get(this.$props.src)
                    .then(o => {
                        this.$store.commit("setTitle", o.data.data.name);

                        let schema = JSON.parse(o.data.data.schema);

                        this.$store.commit("setSource", schema);



                        this.loading = false;
                    })
                    .catch(o => {
                        console.log(o);
                    });
            } else {
                // this.$store.replaceState(newState);
                this.$store.commit("reset", null);
                this.loading = false;
            }
        }
    },
    created() {
        this.init();
    },
    mounted() {},
    data() {
        return {
            loading: true
        };
    },
    components: {
        draggable,
        TableList,
        ElementControl,
        ElementPreview
    },
    computed: {
        ...mapGetters({
            fields: "fields",
            other: "other"
        })
    },
    store
};
</script>
