<template>
    <div :class="type == 'AreaChart' || type == 'LineChart' || type == 'ColumnChart' ? 'chart-element-wide' : type == 'countBox' ? 'count-box' :'chart-element'">
        <component :is="type" v-bind="currentProperties" @changeFilter="changeFilter" :chart_filter="chart_filter" @unFilter="unFilter" :id="id"></component>
    </div>
</template>

<script>
import AreaLine from "./elements/AreaLine";
import Pie from "./elements/Pie";
import TableE from "./elements/Table";
import Radar from "./elements/Radar";
import CountBox from "./elements/CountBox";

export default {
    props: ["src", "id", "chart_filter"],
    data() {
        return {
            currentProperties: null,
            type: ""
        };
    },
    methods: {
        init() {
            axios
                .get(this.$props.src)
                .then(o => {
                    this.currentProperties = JSON.parse(o.data.data.schema);

                    this.type = this.currentProperties.type;
                })
                .catch(o => {
                    console.log(o);
                });
        },
        changeFilter(event) {
            this.$emit("changeFilter", event);
        },
        unFilter() {
            this.$emit("unFilter");
        }
    },

    mounted() {
        this.init();
    },

    components: {
        AreaChart: AreaLine,
        LineChart: AreaLine,
        ColumnChart: AreaLine,
        PieChart: Pie,
        TreeMapChart: Pie,
        FunnelChart: Pie,
        DataTable: TableE,
        RadarChart: Radar,
        countBox: CountBox,
    },
    computed: {
        // currentProperties: function () {
        //     return {
        //         chart_title: this.title,
        //         ...JSON.parse(this.source)
        //     };
        // },
        // type: function () {
        //     let source = JSON.parse(this.source);
        //     return source.type
        // },
    }
};
</script>
