<template>
    <div class="expand">
        <Tabs :animated="false" size="small" class="expand-tab">
            <TabPane label="Үндсэн тохиргоо">
                <Row type="flex">
                    <Col span="24" class="rel-col">
                        <ul>
                            <li>
                                <label>Параметрээс утга авах</label>
                                <Input type="text" placeholder="Параметрийн нэр" v-model="item.filter.param"/>
                            </li>
                            <li>
                                <label>Параметрийн харьцуулалт</label>
                                <Select v-model="item.filter.paramCompareType" placeholder="Харьцуулалт хийх арга"
                                        clearable
                                        filterable>
                                    <Option v-for="item in paramComparisons" :value="item" :key="item">{{ item }}
                                    </Option>
                                </Select>
                            </li>
                            <li>
                                <label>Хажуу талын хайлтад харагдах эсэх</label>
                                <i-switch v-model="item.filter.showSideFilter" size="small"></i-switch>
                            </li>
                        </ul>
                    </Col>
                </Row>
            </TabPane>

            <TabPane label="Өгөгдлийн тохиргоо" v-if="item.filter.type == 'Select'">
                <Row type="flex">
                    <Col span="24" class="rel-col">
                        <div class="title">
                            <h3>Өгөгдлийн холбоос </h3>
                        </div>
                        <ul>
                            <li>
                                <label>Хүснэгт</label>
                                <Select v-model="item.filter.relation.table" placeholder="Хүснэгт сонгох" clearable
                                        filterable @on-change="relationSchema">
                                    <OptionGroup label="Table list">
                                        <Option v-for="item in tableList" :value="item" :key="item.index">{{ item }}
                                        </Option>
                                    </OptionGroup>
                                    <OptionGroup label="View list">
                                        <Option v-for="item in viewList" :value="item" :key="item.index">{{ item }}
                                        </Option>
                                    </OptionGroup>
                                </Select>
                            </li>
                            <li>
                                <label>Холбогдох талбар</label>
                                <Select v-model="item.filter.relation.key" placeholder="Холбогдох талбар" clearable
                                        filterable>
                                    <Option v-for="item in relSchema" :value="item.model" :key="item.index">{{
                                        item.model }}
                                    </Option>
                                </Select>
                            </li>
                            <li>
                                <label>Харагдах талбарууд</label>
                                <Select v-model="item.filter.relation.fields" placeholder="Талбарууд сонгох" clearable
                                        filterable multiple>
                                    <Option v-for="item in relSchema" :value="item.model" :key="item.index">{{
                                        item.model }}
                                    </Option>
                                </Select>
                            </li>
                            <li>
                                <label>Эрэмбэлэх талбар</label>
                                <Select v-model="item.filter.relation.sortField" placeholder="Талбар сонгох" clearable
                                        filterable>
                                    <Option v-for="item in relSchema" :value="item.model" :key="item.index">{{
                                        item.model }}
                                    </Option>
                                </Select>
                            </li>
                            <li>
                                <label></label>
                                <RadioGroup v-model="item.filter.relation.sortOrder">
                                    <Radio label="asc">
                                        <Icon type="arrow-up-c"></Icon>
                                        <span>A-Z</span>
                                    </Radio>
                                    <Radio label="desc">
                                        <Icon type="arrow-down-c"></Icon>
                                        <span>Z-A</span>
                                    </Radio>
                                </RadioGroup>
                            </li>
                            <li>
                                <label>Эцэг багана (Шүүлтэд байгаа)</label>
                                <Select v-model="item.filter.relation.parentFieldOfForm" placeholder="Эцэг багана"
                                        clearable filterable>
                                    <Option v-for="it in schema" :value="it.model" :key="it.index">{{
                                        it.model }}
                                    </Option>
                                </Select>
                            </li>
                            <li>
                                <label>Эцэг багана (Энэ Хүснэгтийн)</label>
                                <Select v-model="item.filter.relation.parentFieldOfTable" placeholder="Эцэг багана"
                                        clearable
                                        filterable>
                                    <Option v-for="it in relSchema" :value="it.model" :key="it.index">{{
                                        it.model }}
                                    </Option>
                                </Select>
                            </li>
                        </ul>

                        <div class="title">
                            <h3>Холбоосийн нөхцөл</h3>
                        </div>

                        <query-builder v-if="relSchema && relSchema.length >=1" @change="changeItemFilter"
                                       :query="item.filter.relation.filter" :fields="relSchema"></query-builder>


                        <div style="height: 400px">
                            <div class="title" >
                                <h3>Холбоосийн нөхцөл  (Хэрэглэгчээс авах)</h3>
                            </div>
                            <div>
                                <Row>
                                    <Col span="10">
                                        <Select v-model="optionSelectFilterWithUser.userField" filterable placeholder="Хэрэглэгчийн багана">
                                            <Option v-for="item in user_fields" :value="item" :key="item">{{ item }}</Option>
                                        </Select>
                                    </Col>
                                    <Col span="10">
                                        <Select v-model="optionSelectFilterWithUser.tableField" filterable placeholder="Шүүлт хийх багана">
                                            <Option v-for="item in relSchema" :value="item.model" :key="item.model">{{ item.model }}</Option>
                                        </Select>
                                    </Col>
                                    <Col span="4">
                                        <Button type="primary" shape="circle" icon="md-add" @click="addSelectUserFilter"></Button>
                                    </Col>
                                </Row>

                                <Row v-for="(condition, index) in item.filter.relation.filterWithUser" :key="index">
                                    <Col span="10">
                                        {{condition.userField}}
                                    </Col>
                                    <Col span="10">
                                        {{condition.tableField}}
                                    </Col>
                                    <Col span="4">
                                        <Button type="primary" shape="circle" icon="ios-trash" @click="deleteSelectUserFilter(index)"></Button>
                                    </Col>
                                </Row>

                            </div>
                        </div>
                    </Col>
                </Row>
            </TabPane>
        </Tabs>
    </div>
</template>

<script>
    import {elementList} from "./elements";
    import {getTableMeta} from "./utils/helpers";
    export default {
        props: ["item", "schema","edit"],
        data() {
            return {
                expanded: false,
                tableList: window.init.dbSchema.tableList,
                viewList: window.init.dbSchema.viewList,
                elementList: elementList,
                relSchema: [],
                paramComparisons: [
                    "equals",
                    "notEqual",
                    "contains",
                    "notContains",
                    "startsWith",
                    "endsWith",
                    "greaterThan",
                    "greaterThanOrEqual",
                    "lessThan",
                    "lessThanOrEqual",
                    "lessThanOrEqual",
                    "inRange",
                ],
                optionSelectFilterWithUser:{
                    userField:null,
                    tableField:null,
                },
                user_fields: window.init.user_fields,
            };
        },
        created() {
            if (this.item.filter.relation.table !== null) {
                this.relationSchema(this.item.filter.relation.table);
            }
        },
        methods: {
            async relationSchema(val) {
                this.relSchema = await getTableMeta(val)
            },
            //Filter event
            changeItemFilter(query) {
                if (query) {
                    this.$props.item.filter.relation.filter = query.sql;
                } else {
                    this.$props.item.filter.relation.filter = undefined;
                }
            },
            addSelectUserFilter(){
                if(!this.$props.item.filter.relation.filterWithUser){
                    this.$props.item.filter.relation.filterWithUser = [];
                }
                this.$props.item.filter.relation.filterWithUser.push({
                    userField:this.optionSelectFilterWithUser.userField,
                    tableField:this.optionSelectFilterWithUser.tableField,
                });

                this.optionSelectFilterWithUser ={
                    userField:null,
                    tableField:null,
                };
            },
            deleteSelectUserFilter(index){
                this.$props.item.filter.relation.filterWithUser.splice(index, 1);
            },
        }
    };
</script>


