<template>
    <component ref="krud" :is="renderTemplate(property.template)" v-bind="property">
        <template slot="left">
            <slot name="left"></slot>
        </template>

        <template slot="nav">
            <slot name="nav"></slot>
        </template>

        <template slot="v-nav">
            <slot name="v-nav"></slot>
        </template>

        <template slot="tooloptions">
            <slot name="toolbuttons"></slot>
        </template>
        <div class="krud-header-right" slot="right">
            <slot name="right"></slot>
        </div>
    </component>
</template>

<script>
    export default {
        props: ["property"],
        methods: {
            renderTemplate(template) {
                return require(`./templates/${template}.vue`).default;
            }
        }
    };
</script>



