<template>
    <section class="page ">
        <graphql :editMode="editMode" :src="src" :onCreate="onCreate" :onUpdate="onUpdate"></graphql>
    </section>
</template>

<script>

    import graphql from "./graphql"
    export default {
        components: {
          graphql
        },
        data() {
            return {
                editMode: false,
                src: ""
            };
        },
        created() {
            let id = this.$route.params.id;
            if (typeof id !== "undefined" && id !== "" && id !== null) {
                this.editMode = true;
                this.src = `/lambda/puzzle/schema/graphql/${this.$route.params.id}`;
            }
        },
        methods: {
            onCreate() {
                this.$router.push("/graphql");
            },
            onUpdate() {
                this.$router.push("/graphql");
            }
        }
    };
</script>
