<template>
  <div class="data-source">

    <div class="data-source-sidebar" style="width: 600px">
      <div class="data-source-header">
        <h3>

          Graphql удирдлага
        </h3>
      </div>
      <div class="data-source-sidebar-wrapper">

        <h5>Нэр</h5>
        <Input v-model="name" placeholder="Нэр..." style="width: 100%"/>
        <h5>Хүснэгт</h5>
        <Select v-model="graphql.table" placeholder="Хүснэгт сонгох" clearable
                filterable
                @on-change="selectTable">
          <OptionGroup label="Table list">
            <Option v-for="item in tableList" :value="item" :key="item.index">{{
                item
              }}
            </Option>
          </OptionGroup>
          <OptionGroup label="View list">
            <Option v-for="item in viewList" :value="item" :key="item.index">{{
                item
              }}
            </Option>
          </OptionGroup>
        </Select>

        <h5>
          ID талбар
        </h5>
        <Select v-model="graphql.identity" filterable placeholder="ID талбар">
          <Option v-for="item in relSchema" :value="item.model" :key="item.model">{{ item.model }}</Option>
        </Select>

        <h5>
          Нуух талбарууд
        </h5>
        <Select v-model="graphql.hidden_columns" filterable multiple placeholder="Нуух талбарууд">
          <Option v-for="item in relSchema" :value="item.model" :key="item.model">{{ item.model }}</Option>
        </Select>

        <h5>Зөвшөөрөх үйлдлүүд</h5>
        <Checkbox v-model="graphql.actions.create">Нэмэх</Checkbox>
        <Checkbox v-model="graphql.actions.update">Засах</Checkbox>
        <Checkbox v-model="graphql.actions.delete">Устгах</Checkbox>

        <h5>Real time subscription</h5>
        <Checkbox v-model="graphql.subscription">Real time subscription</Checkbox>
        <h5>Нэвтрэлт болон хандах эрх</h5>
        <Checkbox v-model="graphql.checkAuth.isLoggedIn">Нэвтэрсэн хүнд харуулна</Checkbox>
        <h5 v-if="graphql.checkAuth.isLoggedIn">Харах боломжтой хандах эрх</h5>
        <h6 v-if="graphql.checkAuth.isLoggedIn">(Эрх тавьж өгөөгүй тохиолдолд бүх хэрэглчид харах боломжтой)</h6>
        <Select graphql.checkAuth.isLoggedIn v-model="graphql.checkAuth.roles" filterable multiple
                placeholder="Харах боломжтой хандах эрх">
          <Option v-for="item in user_roles" :value="item.id" :key="item.id">{{ item.display_name }}</Option>
        </Select>

        <h5>Дэд хүсэгтүүд
          <Button type="primary" shape="circle" icon="md-add" @click="showSub"></Button>
        </h5>
        <Table border :columns="subColumns" :data="graphql.subs"></Table>

      </div>


    </div>
    <div class="data-source-workspace">
      <div class="data-source-header">

        <div class="data-source-header-buttons">

          <Button
              type="success"
              @click="saveSchema"
          >Хадгалах
          </Button>

        </div>

      </div>

      <div
          class="graphql-builder"

      >

      </div>
    </div>
    <Modal
        v-model="showSubForm"
        title="Дэд хүснэгтүүд"
        @on-ok="addSub"
        @on-cancel="cancel">
      <div>
        <h5>Дэд хүснэгт</h5>
        <Select filterable @on-change="selectSubTable" v-model="newSub.graphqlID" placeholder="Нуух талбарууд">
          <Option v-for="item in otherGraphql" :value="item.id" :key="item.name">{{ item.name }}</Option>
        </Select>

        <h5>
          Холбох талбар
        </h5>
        <Select v-model="newSubData.connection_field" filterable placeholder=" Холбох талбар">
          <Option v-for="item in subFields" :value="item.model" :key="item.model">{{ item.model }}</Option>
        </Select>


      </div>
    </Modal>
  </div>
</template>

<script>


import {getTableMeta} from "../../../../../dataform/src/utils/helpers";

export default {
  props: ["onCreate", "onUpdate", "src", "editMode"],
//{"table":"bag", "connection_field":"sumid", "parent_identity":"id"}
  created() {
    this.init();

  },
  methods: {
    cancel() {
      this.showSubForm = false;
    },
    deleteSub(index){
        this.graphql.subs.splice(index, 1);
    },
    addSub() {
      this.graphql.subs.push(JSON.parse(JSON.stringify(this.newSubData)))

      this.newSub = {
        graphqlID: "",
        parent_identity: "",
      }
    },
    showSub() {
      this.showSubForm = true;
    },
    async selectTable(table) {
      this.relSchema = await getTableMeta(table);
    },
    selectSubTable(id) {
      axios.get("/lambda/puzzle/schema/datasource/" + id)
          .then(o => {
            var schema = JSON.parse(o.data.data.schema);
            this.newSubData.table = schema.table
            this.newSubData.parent_identity = schema.identity
            this.subFields = window.init.dbSchema.tableMeta[schema.table];

          })

    },
    async callOtherForms() {
      if (window.otherForms) {
        this.otherForms = window.otherForms;
      } else {
        let res = await axios.get('/lambda/puzzle/schema/graphql');
        this.otherGraphql = res.data.data;
      }
    },
    init() {
      if (this.$props.editMode == true) {
        axios
            .get(this.$props.src)
            .then(o => {
              this.name = o.data.data.name;
              this.graphql = JSON.parse(o.data.data.schema);
              this.selectTable(this.graphql.table);
              this.callOtherForms()
            })
            .catch(o => {
              console.log(o);
            });
      } else {
        this.callOtherForms()
      }
    },
    saveSchema() {

      let data = {
        name: this.name,
        schema: JSON.stringify(this.graphql)
      };
      let submitUrl = this.$props.editMode ?
          this.$props.src :
          `/lambda/puzzle/schema/graphql`;

      axios.post(submitUrl, data).then(o => {
        if (o.data.status) {
          this.$Message.info("Амжилттай хадгаллаа");
          this.$props.onCreate();
          this.graphql = {
            "table": "",
            "identity": "",
            "hidden_columns": [],
            "checkAuth": {"isLoggedIn": false, "roles": []},
            "subs": [],
            "actions": {"create": false, "update": false, "delete": false}
          };
        } else {
          this.$Message.info("Хадгалах үед алдаа гарлаа!");
        }
      });
    }
  },
  data() {
    return {
      tableList: window.init.dbSchema.tableList,
      tableMeta: window.init.dbSchema.tableMeta,
      viewList: window.init.dbSchema.viewList,
      user_roles: window.init.user_roles,
      relSchema: [],
      subFields: [],
      otherGraphql: [],
      showSubForm: false,
      subColumns: [
        {
          title: 'Хүснэгт',
          key: 'table'
        },
        {
          title: 'Эцэг хүснэгтийн ID',
          key: 'parent_identity'
        },
        {
          title: 'Холбор талбар',
          key: 'connection_field'
        },
        {
          title: 'Action',
          key: 'action',
          width: 150,
          align: 'center',
          render: (h, params) => {
            return h('div', [
              h('Button', {
                props: {
                  type: 'error',
                  size: 'small'
                },
                on: {
                  click: () => {
                    this.deleteSub(params.index)
                  }
                }
              }, 'Устгах')
            ]);
          }
        }
      ],
      newSub: {
        graphqlID: "",
        parent_identity: "",
      },
      newSubData: {
        table: "",
        parent_identity: "",
        connection_field: "",
      },
      name: "",
      graphql: {
        "table": "",
        "identity": "",
        "hidden_columns": [],
        "checkAuth": {"isLoggedIn": false, "roles": []},
        "subs": [],
        "actions": {"create": false, "update": false, "delete": false},
        "subscription":false
      }
    };
  },
  mounted() {

  },
  components: {},
  computed: {},
  watch: {}
};
</script>
